import{_ as e,c as a,o as s,e as n}from"./404.md.8d363e7c.js";const y=JSON.parse('{"title":"On Death","description":"","frontmatter":{"title":"On Death","category":"On Event Systems","mention":["BedrockCommands","EziGotFliped"],"nav_order":4},"headers":[{"level":2,"title":"Introduction","slug":"introduction","link":"#introduction","children":[]},{"level":2,"title":"Setup","slug":"setup","link":"#setup","children":[]},{"level":2,"title":"System","slug":"system","link":"#system","children":[]},{"level":2,"title":"Explanation","slug":"explanation","link":"#explanation","children":[]}],"relativePath":"commands/on-death.md"}'),r={name:"commands/on-death.md"},o=n(`<h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-hidden="true">#</a></h2><p>This system will run your desired commands on the event that a player dies.</p><h2 id="setup" tabindex="-1">Setup <a class="header-anchor" href="#setup" aria-hidden="true">#</a></h2><p><em>To be typed in chat:</em><br><code>/scoreboard objectives add alive dummy</code></p><h2 id="system" tabindex="-1">System <a class="header-anchor" href="#system" aria-hidden="true">#</a></h2><p><em>mcfunction / Command Line:</em></p><div class="language- line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">/scoreboard players set @a [scores={alive=!2}] alive 0</span></span>
<span class="line"><span style="color:#A6ACCD;">/scoreboard players set @e [type=player] alive 1</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">#Your Commands Here (example)</span></span>
<span class="line"><span style="color:#A6ACCD;">/execute as @a [scores={alive=0}] run say I died</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">/scoreboard players set @a [scores={alive=0}] alive 2</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><p><img src="https://user-images.githubusercontent.com/99989764/214433884-f718e120-d7fd-47c5-8d0d-89328d02ca23.png" alt="export1674579202856"><br> Here we have used an <code>/execute - say</code> command as an example but you can use any command you prefer and as many as you require.<br> Just make sure to follow the given order and properly add the selector argument <code>scores={alive=0}</code> as shown for your desired commands.</p><h2 id="explanation" tabindex="-1">Explanation <a class="header-anchor" href="#explanation" aria-hidden="true">#</a></h2><p><strong><code>alive=0</code></strong> this means player is dead.<br><strong><code>alive=1</code></strong> this means player is alive.<br><strong><code>alive=2</code></strong> this means player is dead and we have run our desired commands on/from them.<br><br></p><p><strong><code>@a</code></strong> selector will target all players alive/dead so we use it to mark everyone as 0 &#39;dead.&#39;<br></p><blockquote><p>Note; we will ignore 2 or it will end up making the commands execute on dead players again. We only want our commands to execute once.<br><br></p></blockquote><p><strong><code>@e</code></strong> selector on the other hand will only target players who are alive, so we can use this to mark all alive players 1 &#39;alive.&#39;<br><br></p><p>Now that dead players are 0 and alive players are 1 we can use this knowledge to run our desired commands on the dead players.</p><blockquote><p>Keep in mind we need to set their score to 2 after or otherwise the commands will keep executing till they respawn.<br><br><br></p></blockquote><p><a href="https://discord.gg/SYstTYx5G5" target="_blank" rel="noreferrer">Sourced By Bedrock Commands | Community &amp; Archive</a></p>`,16),t=[o];function l(i,p,c,d,m,u){return s(),a("div",null,t)}const b=e(r,[["render",l]]);export{y as __pageData,b as default};
