import{_ as a,e as o,d as i,Q as t}from"./chunks/framework.CuLWBCQE.js";const _=JSON.parse('{"title":"Playanimation","description":"Some useful playanimation commands","frontmatter":{"title":"Playanimation","category":"Commands","mentions":["BedrockCommands","zheaEvyline"],"description":"Some useful playanimation commands"},"headers":[{"level":2,"title":"Introduction","slug":"introduction","link":"#introduction","children":[]},{"level":2,"title":"Syntax","slug":"syntax","link":"#syntax","children":[]},{"level":2,"title":"List of Useful Playanimation Commands","slug":"list-of-useful-playanimation-commands","link":"#list-of-useful-playanimation-commands","children":[{"level":3,"title":"Body Animations","slug":"body-animations","link":"#body-animations","children":[]},{"level":3,"title":"Root Animations","slug":"root-animations","link":"#root-animations","children":[]},{"level":3,"title":"Head Animations","slug":"head-animations","link":"#head-animations","children":[]}]}],"relativePath":"commands/playanimation.md","filePath":"commands/playanimation.md"}'),n={name:"commands/playanimation.md"},e=t('<h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h2><p><a href="https://discord.gg/SYstTYx5G5" target="_blank" rel="noreferrer">Sourced by the Bedrock Commands Community Discord</a></p><p>In this page, we will look at some <code>/playanimation</code> commands that can be applied to almost any entity, making them versatile and useful for various scenarios.</p><p>Before diving into the commands, we recommend reviewing the <a href="https://minecraft.wiki/w/Commands/playanimation" target="_blank" rel="noreferrer">Minecraft Wiki Playanimation Command Guide</a> to understand the basics of how the command works.</p><h2 id="syntax" tabindex="-1">Syntax <a class="header-anchor" href="#syntax" aria-label="Permalink to &quot;Syntax&quot;">​</a></h2><p><code>playanimation &lt;entity: target&gt; &lt;animation: string&gt; [next_state: string] [blend_out_time: float] [stop_expression: string] [controller: string]</code></p><h2 id="list-of-useful-playanimation-commands" tabindex="-1">List of Useful Playanimation Commands <a class="header-anchor" href="#list-of-useful-playanimation-commands" aria-label="Permalink to &quot;List of Useful Playanimation Commands&quot;">​</a></h2><ul><li>These commands can be applied to nearly any entity, including players.</li><li>All variables are initialized as zero in the examples but can be adjusted as needed.</li><li>Some commands use multiple variables. You can add or remove variables based on your requirements.</li><li>Controller names (e.g., <code>wiki:body_yrot</code>) enable stacking animations without overwriting previous ones. <ul><li>You are free to change the namespace (<code>wiki</code>) or the controller names to your liking.</li></ul></li></ul><h3 id="body-animations" tabindex="-1">Body Animations <a class="header-anchor" href="#body-animations" aria-label="Permalink to &quot;Body Animations&quot;">​</a></h3><ol><li>Rotate the body of the entity along the Y-axis (excluding limbs and head): <ul><li><code>/playanimation @a animation.player.attack.rotations none 0 &quot;v.attack_body_rot_y=0;&quot; wiki:body_yrot</code></li></ul></li><li>Rotate the body of the entity along the Z-axis (excluding limbs and head): <ul><li><code>/playanimation @a animation.wolf.shaking none 0 &quot;v.body_rot_z=0;&quot; wiki:body_zrot</code></li></ul></li></ol><h3 id="root-animations" tabindex="-1">Root Animations <a class="header-anchor" href="#root-animations" aria-label="Permalink to &quot;Root Animations&quot;">​</a></h3><ol><li>Rotate the entire entity along the X-axis and Z-axis: <ul><li><code>/playanimation @a animation.ender_dragon.setup none 0 &quot;v.clamped_pitch=0;v.clamped_roll=0;&quot; wiki:root_xrot_yrot</code></li></ul></li><li>Offset the entire entity&#39;s position along the X, Y, and Z axes: <ul><li><code>/playanimation @a animation.minecart.move none 0 &quot;v.rail_offset.x=0;v.rail_offset.y=0;v.rail_offset.z=0;&quot; wiki:root_pos</code></li></ul></li></ol><h3 id="head-animations" tabindex="-1">Head Animations <a class="header-anchor" href="#head-animations" aria-label="Permalink to &quot;Head Animations&quot;">​</a></h3><ol><li>Offset or rotate the entity&#39;s head: <ul><li><code>/playanimation @a animation.ender_dragon.neck_head_movement none 0 &quot;v.head_position_x=0;v.head_position_y=0;v.head_position_z=0;v.head_rotation_x=0;v.head_rotation_y=0;v.head_rotation_z=0;&quot; wiki:head_pos_rot</code></li></ul></li></ol>',14),l=[e];function s(r,d,m,c,u,h){return i(),o("div",null,l)}const p=a(n,[["render",s]]);export{_ as __pageData,p as default};
