import{_ as a,e as i,d as e,Q as n}from"./chunks/framework.CuLWBCQE.js";const y=JSON.parse('{"title":"Playanimation","description":"Some useful playanimation commands","frontmatter":{"title":"Playanimation","category":"Commands","mentions":["BedrockCommands","zheaEvyline"],"description":"Some useful playanimation commands"},"headers":[{"level":2,"title":"Introduction","slug":"introduction","link":"#introduction","children":[]},{"level":2,"title":"Syntax","slug":"syntax","link":"#syntax","children":[]},{"level":2,"title":"List of Useful Playanimation Commands","slug":"list-of-useful-playanimation-commands","link":"#list-of-useful-playanimation-commands","children":[{"level":3,"title":"Body Animations","slug":"body-animations","link":"#body-animations","children":[]},{"level":3,"title":"Root Animations","slug":"root-animations","link":"#root-animations","children":[]},{"level":3,"title":"Head Animations","slug":"head-animations","link":"#head-animations","children":[]}]}],"relativePath":"commands/playanimation.md","filePath":"commands/playanimation.md"}'),t={name:"commands/playanimation.md"},o=n('<h2 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h2><p><a href="https://discord.gg/SYstTYx5G5" target="_blank" rel="noreferrer">Sourced by the Bedrock Commands Community Discord</a></p><p>In this page, we will look at some <code>/playanimation</code> commands that can be applied to almost any entity, making them versatile and useful for various scenarios.</p><p>Before diving into the commands, we highly recommend reading the <a href="https://minecraft.wiki/w/Commands/playanimation" target="_blank" rel="noreferrer">Minecraft Wiki Playanimation Command Guide</a> to understand the basics of how the command works. Reviewing the <a href="https://bedrock.dev/docs/stable/Molang" target="_blank" rel="noreferrer">Molang Documentation</a> is also recommended.</p><h2 id="syntax" tabindex="-1">Syntax <a class="header-anchor" href="#syntax" aria-label="Permalink to &quot;Syntax&quot;">​</a></h2><p><code>playanimation &lt;entity: target&gt; &lt;animation: string&gt; [next_state: string] [blend_out_time: float] [stop_expression: string] [controller: string]</code></p><h2 id="list-of-useful-playanimation-commands" tabindex="-1">List of Useful Playanimation Commands <a class="header-anchor" href="#list-of-useful-playanimation-commands" aria-label="Permalink to &quot;List of Useful Playanimation Commands&quot;">​</a></h2><ul><li>These commands can be applied to nearly any entity, including players.</li><li>All variables are initialized as zero in the examples but can be adjusted as needed.</li><li>Some commands use multiple variables. You can add or remove variables based on your requirements.</li><li>Controller names (e.g., <code>wiki:body_yrot</code>) enable stacking animations without overwriting previous ones. <ul><li>You are free to change the namespace (<code>wiki</code>) or the controller names to your liking.</li></ul></li></ul><h3 id="body-animations" tabindex="-1">Body Animations <a class="header-anchor" href="#body-animations" aria-label="Permalink to &quot;Body Animations&quot;">​</a></h3><ol><li>Rotate the body of the entity along the Y-axis (excluding limbs and head):<div class="language-yaml vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/playanimation @a animation.player.attack.rotations none 0 &quot;v.attack_body_rot_y=0;&quot; wiki:body_yrot</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li>Rotate the body of the entity along the Z-axis (excluding limbs and head):<div class="language-yaml vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/playanimation @a animation.wolf.shaking none 0 &quot;v.body_rot_z=0;&quot; wiki:body_zrot</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li></ol><h3 id="root-animations" tabindex="-1">Root Animations <a class="header-anchor" href="#root-animations" aria-label="Permalink to &quot;Root Animations&quot;">​</a></h3><ol><li>Rotate the entire entity along the X-axis and Z-axis:<div class="language-yaml vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/playanimation @a animation.ender_dragon.setup none 0 &quot;v.clamped_pitch=0;v.clamped_roll=0;&quot; wiki:root_xrot_yrot</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li>Offset the entire entity&#39;s position along the X, Y, and Z axes:<div class="language-yaml vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/playanimation @a animation.minecart.move none 0 &quot;v.rail_offset.x=0;v.rail_offset.y=0;v.rail_offset.z=0;&quot; wiki:root_pos</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li></ol><h3 id="head-animations" tabindex="-1">Head Animations <a class="header-anchor" href="#head-animations" aria-label="Permalink to &quot;Head Animations&quot;">​</a></h3><ol><li>Offset or rotate the entity&#39;s head:<div class="language-yaml vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/playanimation @a animation.ender_dragon.neck_head_movement none 0 &quot;v.head_position_x=0;v.head_position_y=0;v.head_position_z=0;v.head_rotation_x=0;v.head_rotation_y=0;v.head_rotation_z=0;&quot; wiki:head_pos_rot</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li></ol>',14),s=[o];function l(r,d,m,c,h,p){return e(),i("div",null,s)}const b=a(t,[["render",l]]);export{y as __pageData,b as default};
