import{_ as i,e as a,t as o,Q as e,d as n,D as s}from"./chunks/framework.CuLWBCQE.js";const f=JSON.parse('{"title":"Style Guide","description":"Officially supported Bedrock-Wiki style guide for add-on creation.","frontmatter":{"title":"Style Guide","mentions":["wiki","solvedDev","MedicalJewel105","ChibiMango","zheaEvyline"],"description":"Officially supported Bedrock-Wiki style guide for add-on creation."},"headers":[{"level":2,"title":"Folder Structure","slug":"folder-structure","link":"#folder-structure","children":[]},{"level":2,"title":"Identifiers","slug":"identifiers","link":"#identifiers","children":[]},{"level":2,"title":"File and Folder names","slug":"file-and-folder-names","link":"#file-and-folder-names","children":[]},{"level":2,"title":"Namespaces","slug":"namespaces","link":"#namespaces","children":[]},{"level":2,"title":"Sub-indexing","slug":"sub-indexing","link":"#sub-indexing","children":[]},{"level":2,"title":"Groups and Events should complement each other","slug":"groups-and-events-should-complement-each-other","link":"#groups-and-events-should-complement-each-other","children":[]},{"level":2,"title":"Short-Names should be Generic","slug":"short-names-should-be-generic","link":"#short-names-should-be-generic","children":[]},{"level":2,"title":"Functions should be nested","slug":"functions-should-be-nested","link":"#functions-should-be-nested","children":[]},{"level":2,"title":"Scoreboards and Tags","slug":"scoreboards-and-tags","link":"#scoreboards-and-tags","children":[]},{"level":2,"title":"Group animations files when possible","slug":"group-animations-files-when-possible","link":"#group-animations-files-when-possible","children":[]},{"level":2,"title":"Split textures by path, not name","slug":"split-textures-by-path-not-name","link":"#split-textures-by-path-not-name","children":[]},{"level":2,"title":".lang File Comments","slug":"lang-file-comments","link":"#lang-file-comments","children":[]},{"level":2,"title":"Acronyms when discussing","slug":"acronyms-when-discussing","link":"#acronyms-when-discussing","children":[]},{"level":2,"title":"Blocks/Items Format Order","slug":"blocks-items-format-order","link":"#blocks-items-format-order","children":[{"level":3,"title":"Blocks","slug":"blocks","link":"#blocks","children":[]},{"level":3,"title":"Items","slug":"items","link":"#items","children":[]}]},{"level":2,"title":"Custom Components Variable Name","slug":"custom-components-variable-name","link":"#custom-components-variable-name","children":[]}],"relativePath":"meta/style-guide.md","filePath":"meta/style-guide.md"}'),l={name:"meta/style-guide.md"},r=e('<p>This document will present the officially supported Bedrock-Wiki style guide for add-on creation. This guide aims to promote best practices while creating add-ons and create a consistent format for everyone to follow.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The style guide is a living, breathing document, which will evolve as add-on creation evolves. Please get in touch if you think something needs to be updated or changed!</p></div><h2 id="folder-structure" tabindex="-1">Folder Structure <a class="header-anchor" href="#folder-structure" aria-label="Permalink to &quot;Folder Structure&quot;">​</a></h2><ul><li>No spaces in your file paths. <code>use_underscores</code>.</li><li>No <code>CAPITALS</code> in your identifiers, file names, or folder names.</li><li>The total character length of any path must not exceed 80 characters (console limitation).</li><li>Content folders should use consistent pluralization: Don&#39;t mix and match.</li></ul><h2 id="identifiers" tabindex="-1">Identifiers <a class="header-anchor" href="#identifiers" aria-label="Permalink to &quot;Identifiers&quot;">​</a></h2><p>Do not use identifiers that begin with a number, and especially don&#39;t use an identifier that is <em>only</em> a number. This applies to entities, component_groups, events, and anything else that takes a <code>namespace:name</code> pair.</p><h2 id="file-and-folder-names" tabindex="-1">File and Folder names <a class="header-anchor" href="#file-and-folder-names" aria-label="Permalink to &quot;File and Folder names&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Concept</th><th>Example Identifier</th></tr></thead><tbody><tr><td>Behavior Pack</td><td>dragons_BP</td></tr><tr><td>Resource Pack</td><td>dragons_RP</td></tr><tr><td>Geometry</td><td>dragon.geo.json</td></tr><tr><td>Animation</td><td>dragon.animation.json</td></tr><tr><td>Animation Controller</td><td>dragon.ac.json</td></tr><tr><td>RP Entity</td><td>dragon.ce.json</td></tr><tr><td>BP Entity</td><td>dragon.se.json</td></tr><tr><td>Item 1.16.100+</td><td>dragon_tooth.item.json</td></tr><tr><td>BP Item</td><td>dragon_tooth.item.bp.json</td></tr><tr><td>RP Item</td><td>dragon_tooth.item.rp.json</td></tr><tr><td>Render Controller</td><td>dragon.rc.json</td></tr><tr><td>Loot Table</td><td>dragon.loot.json</td></tr><tr><td>Recipe</td><td>dragon_saddle.recipe.json</td></tr><tr><td>Spawn Rules</td><td>dragon.spawn.json</td></tr><tr><td>Trade Table</td><td>dragon.trade.json</td></tr><tr><td>Particles</td><td>dragon_magic.particle.json</td></tr><tr><td>Texture</td><td>dragon.png</td></tr><tr><td>Gametest</td><td>dragonTest.js</td></tr></tbody></table><h2 id="namespaces" tabindex="-1">Namespaces <a class="header-anchor" href="#namespaces" aria-label="Permalink to &quot;Namespaces&quot;">​</a></h2><p>A suitable namespace should be unique to you or your team. Something like <code>mob</code> or <code>cars</code> or <code>content</code> or <code>custom</code> would be a <strong>bad</strong> namespace since another developer might come up with the same namespace as you.</p><p><code>minecraft</code> and <code>minecon</code> are reserved. Don&#39;t use these.</p><p>For personal projects, use a convenient version of your player name, and for team projects, use a suitable version of your team name.</p><p>When multiple developers work on a project together, the namespace should always be shared. If credit is required, use sub-indexing: <code>minetite.wiki:dragon</code></p><p>Where to use namespaces:</p><ul><li>entities</li><li>particles</li><li>component-groups</li><li>events</li></ul><p>When not to use namespaces:</p><ul><li>do not include your namespace in any folder path or file-name</li></ul><h2 id="sub-indexing" tabindex="-1">Sub-indexing <a class="header-anchor" href="#sub-indexing" aria-label="Permalink to &quot;Sub-indexing&quot;">​</a></h2><p>Sub indexing is the use of <code>.</code> to separate chained concepts. Sub-indexing should go in descending order from big to small:</p><p>✔️ <code>animation.controller.dragon.flying.taking_off</code></p><p>❌ <code>animation.controller.dragon_take_off_flying</code></p><p>When using sub-indexing, use <code>_</code> as space, not another <code>.</code>.</p><p>✔️ <code>animation.controller.dragon.flying.taking_off</code></p><p>❌ <code>animation.controller.dragon.flying.taking.off</code></p><p>You can use sub-indexing in your entities: <code>wiki:dragon.drake</code></p><h2 id="groups-and-events-should-complement-each-other" tabindex="-1">Groups and Events should complement each other <a class="header-anchor" href="#groups-and-events-should-complement-each-other" aria-label="Permalink to &quot;Groups and Events should complement each other&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Group</th><th>Event</th></tr></thead><tbody><tr><td>wiki:wild</td><td>✔️ wiki:become_wild</td></tr><tr><td>wiki:wild</td><td>❌ wiki:wild</td></tr><tr><td>wiki:tame</td><td>✔️ wiki:on_tame</td></tr><tr><td>wiki:tame</td><td>❌ wiki:tame</td></tr></tbody></table><h2 id="short-names-should-be-generic" tabindex="-1">Short-Names should be Generic <a class="header-anchor" href="#short-names-should-be-generic" aria-label="Permalink to &quot;Short-Names should be Generic&quot;">​</a></h2><p>Short-names are file-specific identifiers, which are used to map between an identifier and a pretty name. They are handy because they allow us to re-use animation controllers and render controllers. For this reason, your short-names should be generic.</p><p>✔️ <code>&quot;sit&quot;: &quot;animation.dragon.sit&quot;</code></p><p>❌ <code>&quot;dragon_sitting&quot;: &quot;animation.dragon.sit&quot;</code></p><p>When we make short-names of this form, we can use a generic &quot;sit&quot; animation controller for all of them since we can use the <code>sit</code> short-name to play the sit animation.</p><h2 id="functions-should-be-nested" tabindex="-1">Functions should be nested <a class="header-anchor" href="#functions-should-be-nested" aria-label="Permalink to &quot;Functions should be nested&quot;">​</a></h2><p>You can put functions in folders to achieve this.</p><p>✔️ <code>function teleport/zone/hell</code></p><p>❌ <code>function teleport_hellzone</code></p><h2 id="scoreboards-and-tags" tabindex="-1">Scoreboards and Tags <a class="header-anchor" href="#scoreboards-and-tags" aria-label="Permalink to &quot;Scoreboards and Tags&quot;">​</a></h2><ul><li>Scoreboard objectives should be named using <code>snake_case</code>, while scoreboard fake-player-names should use <code>camelCase</code>. This distinction helps in easily differentiating between the two when typing out scoreboard commands.</li><li>Like scoreboard fake-player-names, tags should also use <code>camelCase</code> as they have limited interaction with each other and don&#39;t really need further distinction.</li></ul><p><strong>Example Tag Names:</strong></p><ul><li><code>admin</code></li><li><code>inHub</code></li><li><code>inLobby</code></li><li><code>abilityFireTrail</code></li><li><code>abilityWallClimb</code></li></ul><p><em>Only alphanumeric characters.</em></p><p><strong>Example Fake Player Names:</strong></p><ul><li><code>alivePlayer</code></li><li><code>zombieHorse</code></li><li><code>oresEmerald</code></li><li><code>oresDiamond</code></li><li><code>oresDeepslateDiamond</code></li></ul><p><em>Only alphanumeric characters.</em></p><p><strong>Example Objective Names:</strong></p><ul><li><code>world_timer</code></li><li><code>entity_timer</code></li><li><code>abilities.fire_trail</code></li><li><code>abilities.wall_climb</code></li><li><code>abilities.ice_blast</code></li></ul><p><em>Only alphanumeric characters, (<code>_</code>) and (<code>.</code>).</em></p><p>Dot notation (<code>dot.notation</code>) may be used to represent groups or categories. However, avoid over use to prevent cluttering and impacting readability.</p><h2 id="group-animations-files-when-possible" tabindex="-1">Group animations files when possible <a class="header-anchor" href="#group-animations-files-when-possible" aria-label="Permalink to &quot;Group animations files when possible&quot;">​</a></h2><p>Example:</p>',50),d=e(`<div class="language-json vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">json</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">{</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">    &quot;format_version&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;1.8.0&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">    &quot;animations&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: {</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">        &quot;animation.dragon.sit&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: {</span><span style="--shiki-light:#B31D28;--shiki-dark:#FDAEB7;--shiki-light-font-style:italic;--shiki-dark-font-style:italic;">...</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">},</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">        &quot;animation.dragon.fly&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: {</span><span style="--shiki-light:#B31D28;--shiki-dark:#FDAEB7;--shiki-light-font-style:italic;--shiki-dark-font-style:italic;">...</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">},</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">        &quot;animation.dragon.roar&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: {</span><span style="--shiki-light:#B31D28;--shiki-dark:#FDAEB7;--shiki-light-font-style:italic;--shiki-dark-font-style:italic;">...</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">},</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  }</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h2 id="split-textures-by-path-not-name" tabindex="-1">Split textures by path, not name <a class="header-anchor" href="#split-textures-by-path-not-name" aria-label="Permalink to &quot;Split textures by path, not name&quot;">​</a></h2><p>✔️ <code>textures/dragon/red</code></p><p>❌ <code>textures/dragon_red_skin</code></p><p>✔️ <code>textures/npc/dragon_hunter/archer</code></p><p>❌ <code>textures npc/dragon_hunter_archer</code></p><h2 id="lang-file-comments" tabindex="-1">.lang File Comments <a class="header-anchor" href="#lang-file-comments" aria-label="Permalink to &quot;.lang File Comments&quot;">​</a></h2><p>Comments intended for the localizer should always be in-line, in the following format:</p><p><code>the.key=The string&lt;\\t&gt;## Comment, intended for the one localizing.</code></p><p><code>&lt;\\t&gt;</code> represents a tab-character.</p><p>Own-line comments can be used for organizational purposes but should not store localization-critical information.</p><h2 id="acronyms-when-discussing" tabindex="-1">Acronyms when discussing <a class="header-anchor" href="#acronyms-when-discussing" aria-label="Permalink to &quot;Acronyms when discussing&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Acronym</th><th>Concept</th></tr></thead><tbody><tr><td>BP</td><td>Behavior Pack</td></tr><tr><td>RP</td><td>Resource pack</td></tr><tr><td>VRP</td><td>Vanilla Resource Pack</td></tr><tr><td>VBP</td><td>Vanilla Behavior Pack</td></tr><tr><td>AC</td><td>Animation Controller</td></tr><tr><td>RPAC</td><td>Resource Pack Animation Controller</td></tr><tr><td>BPAC</td><td>Behavior Pack Animation Controller</td></tr><tr><td>BB</td><td>Blockbench</td></tr><tr><td>BDS</td><td>Bedrock Dedicated Server</td></tr><tr><td>FPV</td><td>First Person View</td></tr><tr><td>RD</td><td>Render Dragon</td></tr><tr><td>VSCode</td><td>Visual Studio Code</td></tr><tr><td>SP</td><td>Skin Pack</td></tr></tbody></table><h2 id="blocks-items-format-order" tabindex="-1">Blocks/Items Format Order <a class="header-anchor" href="#blocks-items-format-order" aria-label="Permalink to &quot;Blocks/Items Format Order&quot;">​</a></h2><p>Blocks and Items should follow the format order below. This would particularly help with understanding that components that appear later in the file are what will be applied in regards to block permutations.</p><h3 id="blocks" tabindex="-1">Blocks <a class="header-anchor" href="#blocks" aria-label="Permalink to &quot;Blocks&quot;">​</a></h3><ul><li>format_version</li><li>minecraft:block <ul><li>description <ul><li>identifier</li><li>menu_category</li><li>states</li><li>traits</li></ul></li><li>components</li><li>permutations <ul><li>condition</li><li>components</li></ul></li></ul></li></ul><h3 id="items" tabindex="-1">Items <a class="header-anchor" href="#items" aria-label="Permalink to &quot;Items&quot;">​</a></h3><ul><li>format_version</li><li>minecraft:item <ul><li>description <ul><li>identifier</li><li>menu_category</li></ul></li><li>components</li></ul></li></ul><h2 id="custom-components-variable-name" tabindex="-1">Custom Components Variable Name <a class="header-anchor" href="#custom-components-variable-name" aria-label="Permalink to &quot;Custom Components Variable Name&quot;">​</a></h2><p>PascalCase should be used with BlockComponent or ItemComponent as a suffix. As an example, <code>MeltableBlockComponent</code> rather than <code>meltable</code>. This would help to differentiate what we&#39;re using in <code>registerCustomComponent</code> and what we&#39;re using as values elsewhere.</p>`,21);function c(h,p,u,m,g,b){const t=s("CodeHeader");return n(),a("div",null,[r,o(t),d])}const y=i(l,[["render",c]]);export{f as __pageData,y as default};
