import{_ as e,c as o,o as a,U as t}from"./chunks/framework.CiuNV1Mk.js";const y=JSON.parse('{"title":"Privacy Policy","description":"Bedrock Wiki Privacy Policy.","frontmatter":{"title":"Privacy Policy","hidden":true,"show_toc":true,"show_contributors":false,"description":"Bedrock Wiki Privacy Policy."},"headers":[],"relativePath":"privacy.md","filePath":"privacy.md","lastUpdated":1724330024000}'),i={name:"privacy.md"},r=t('<p><a href="/">wiki.bedrock.dev</a> is created and maintained by the Bedrock-OSS organization. This privacy policy will explain how our organization uses the personal data we collect from you when you use our website. By using the wiki.bedrock.dev site, you agree to this policy.</p><h2 id="what-data-do-we-collect" tabindex="-1">What data do we collect? <a class="header-anchor" href="#what-data-do-we-collect" aria-label="Permalink to &quot;What data do we collect?&quot;">​</a></h2><p>Bedrock-OSS collects the following information:</p><ul><li>First two fields of your IP address (e.g. 8.8.0.0 instead of 8.8.8.8)</li><li>Date and time</li><li>Title and URL of the page visited</li><li>URL of the referring page</li><li>Screen resolution</li><li>Timezone</li><li>Files downloaded from the site</li><li>Links to an outside domain clicked</li><li>Time taken for pages to load</li><li>Approximate location and region</li><li>Browser language</li><li>Browser User-Agent</li></ul><h2 id="how-do-we-collect-your-data" tabindex="-1">How do we collect your data? <a class="header-anchor" href="#how-do-we-collect-your-data" aria-label="Permalink to &quot;How do we collect your data?&quot;">​</a></h2><p>You directly provide us with all the data we collect. We collect data when you:</p><ul><li>Open any page on the wiki.bedrock.dev domain</li><li>Click any external link</li></ul><h2 id="how-will-we-use-your-data" tabindex="-1">How will we use your data? <a class="header-anchor" href="#how-will-we-use-your-data" aria-label="Permalink to &quot;How will we use your data?&quot;">​</a></h2><p>Bedrock-OSS collects your data for the purpose of improving the site and evaluating the popularity of different pages and the site as a whole over time.</p><ul><li>Location, region and language are recorded to compare the use of the site in different languages</li><li>Page visited is used to evaluate the popularity of pages and influence future work on the site</li><li>Screen resolution is used to help prioritize development for different target devices</li></ul><p>Bedrock-OSS does not share your data with any other entities except in broad numbers, such as the total number of views a page has received in a week.</p><h2 id="how-do-we-store-your-data" tabindex="-1">How do we store your data? <a class="header-anchor" href="#how-do-we-store-your-data" aria-label="Permalink to &quot;How do we store your data?&quot;">​</a></h2><p>Bedrock-OSS stores all analytics data on a private server accessible only to organization leaders.</p><p>Your data remains on our servers for 6 months, after which it is erased.</p><h2 id="what-are-your-data-protection-rights" tabindex="-1">What are your data protection rights? <a class="header-anchor" href="#what-are-your-data-protection-rights" aria-label="Permalink to &quot;What are your data protection rights?&quot;">​</a></h2><p>At Bedrock-OSS, we would like to ensure you are fully aware of your data protection rights.</p><p>In the collection of your data, we do not separate individual users, but store each request separately.</p><iframe style="width:100%;" src="https://hopper.bedrock.dev/wikihopper/index.php?module=CoreAdminHome&amp;action=optOut&amp;language=en&amp;backgroundColor=4d4d4d&amp;fontColor=ffffff&amp;fontSize=&amp;fontFamily="></iframe><h2 id="how-do-we-use-cookies" tabindex="-1">How do we use cookies? <a class="header-anchor" href="#how-do-we-use-cookies" aria-label="Permalink to &quot;How do we use cookies?&quot;">​</a></h2><p>Our site uses cookies to improve user experience, such as storing the user&#39;s preferred color scheme. We do not use any cookies for tracking or analytics, unless you have prevented the site from tracking you above, in which case we use a cookie to remember your choice.</p><h2 id="privacy-policies-of-other-websites" tabindex="-1">Privacy Policies of other websites <a class="header-anchor" href="#privacy-policies-of-other-websites" aria-label="Permalink to &quot;Privacy Policies of other websites&quot;">​</a></h2><p>Our site contains links to other sites, which operate under different policies. We are not responsible for the privacy policies of other sites.</p><h2 id="changes-to-our-privacy-policy" tabindex="-1">Changes to our Privacy Policy <a class="header-anchor" href="#changes-to-our-privacy-policy" aria-label="Permalink to &quot;Changes to our Privacy Policy&quot;">​</a></h2><p>We try to keep our privacy policy updated. This page was last updated on 18 January 2022.</p><h2 id="contact-us" tabindex="-1">Contact us <a class="header-anchor" href="#contact-us" aria-label="Permalink to &quot;Contact us&quot;">​</a></h2><p>To contact us, join our <a href="/discord.html">discord server</a> or send an email to <a href="mailto:SirLich.business@gmail.com" target="_blank" rel="noreferrer">SirLich.business@gmail.com</a>.</p>',26),s=[r];function l(c,n,d,h,u,p){return a(),o("div",null,s)}const f=e(i,[["render",l]]);export{y as __pageData,f as default};
